import './index.scss';
import axios from 'axios';
import { useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'
import Loader from 'react-loaders';

const Contact = () => {

    const URL = "http://localhost:8000/contact";

    const [form, setForm] = useState({
        name:'',
        email:'',
        subjet:'',
        message:''
    })

    const handleForm = (e)=>{
        const newForm = {...form}
        newForm[e.target.id] = e.target.value
        setForm(newForm)
    }

    const sendEmail = async (e) => {
        e.preventDefault()

        axios.post(URL, {

            name: form.name,
            email: form.email,
            subjet: form.subjet,
            message: form.message

        }).then(()=>{
            
            //alert('¡Mensaje enviado!') 
            //window.location.reload(false)},
            },
        () => {
            console.log('hola')
            //e.preventDefault();
            //alert('error')
            //window.location.reload(true)
            //window.location.reload(false)
        })
        
        
        /*.then(
            () => {
                alert('¡Mensaje enviado!')
                window.location.reload(false)
            },
            () => {
                //alert('No se pudo enviar el formulario')
                console.log('error')
            }
        )*/
		}

    return (
        <>
            <div className='Container Contact'>
                
                <div className='Text-Zone'>

                    <h1>
                        CONTACTO
                    </h1>
                    
                    <p>
                        Si quieres tener tu propia 
                        página web o darle 
                        mantenimiento a la que ya tienes, 
                        no dudes en contactarme. También estoy 
                        abierto a escuchar propuestas fulltime o cualquier
                        otro tipo de consultas.
                    </p>
                </div>
                <div className='Contact-Form'>
                    <form onSubmit={sendEmail}>
                        <ul>
                            <li>
                                <label htmlFor='name'>Nombre</label>
                                <input 
                                    onChange={(e)=>handleForm(e)}
                                    type='text'
                                    id='name'
                                    name='name'
                                    placeholder='Nombre'
                                    value={form.name}
                                    required
                                />
                            </li>
                            <li>
                                <label htmlFor='email'>Email</label>
                                <input 
                                    onChange={(e)=>handleForm(e)}
                                    type='email'
                                    id='email'
                                    name='email'
                                    placeholder='Email'
                                    value={form.email}
                                    required
                                />
                            </li>
                            <li className='Full'>
                                <label htmlFor='subjet'>Asunto</label>
                                <input 
                                    onChange={(e)=>handleForm(e)}
                                    type='text'
                                    id='subjet'
                                    name='subjet'
                                    placeholder='Asunto'
                                    value={form.subjet}
                                    required
                                />
                            </li>
                            <li className='Full'>
                                <label htmlFor='message'>Mensaje</label>
                                <textarea 
                                    onChange={(e)=>handleForm(e)}
                                    id='message'
                                    name='message'
                                    placeholder='Mensaje'
                                    value={form.message}
                                    required
                                >
                                </textarea>
                            </li>
                            <li className='Full'>
                                <input 
                                    type='submit'
                                    className='Flat-Button'
                                    value='ENVIAR'
                                />
                            </li>
                        </ul>
                    </form>
                </div>
                <div className='Map'>
                    <MapContainer center={[-34.5875522, -58.3972196]} zoom={16}>
                        <TileLayer url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'/>
                        <Marker position={[-34.5875522, -58.3972196]}>
                            <Popup>
                                Actualmente me encuentro en el barrio de Recoleta
                            </Popup>
                        </Marker>
                    </MapContainer>
                </div>
            </div>
            <Loader type='ball-rotate'/>
        </>
    );
}

export default Contact;
